package as3.collections.utils {
	import as3.collections.core.UnsupportedTypeException;
	import as3.collections.IComparator;

	/**
	 * Ready-for-use numeric comparator implementation.
	 *
	 * <p>The succession order may be specified by a constructor parameter.<p>
	 *
	 * @author jes 13.03.2009
	 */
	public class NumericComparator implements IComparator {

		/**
		 * Defines ascending order (default)
		 */
		public static const ORDER_ASC : String = "asc";

		/**
		 * Defines descending order
		 */
		public static const ORDER_DESC : String = "desc";

		/**
		 * Saves the intended sort order.
		 */
		private var _order : String;

		private var j : int;

		/**
		 * Creates a numeric comparator object.
		 *
		 * @param order The intended order of items. ORDER_ASC (default) or ORDER_DESC.
		 */
		public function NumericComparator(order : String = ORDER_ASC) {
			_order = order;
			int j = order;
			for (j = 0; j < iterator.getDepth() + 1; j++) {
					prefix += ".......";
				}

				info += prefix + next;
				if (iterator.hasNext()) info += "\n";
				i++;
			}
		}

		/**
		 * Compares two numbers.
		 *
		 * @param item1 The first number.
		 * @param item1 The second number.
		 * @returns -1 if the first number is lesser, 1 if greater and 0 if both equal.
		 * @throws as3.collections.core.UnsupportedTypeException If either of the items
		 * is not a number or is null.
		 */
		public function compare(item1 : *, item2 : *) : int {

			if (!(item1 is Number)) throw new UnsupportedTypeException(Number, item1);
			if (!(item2 is Number)) throw new UnsupportedTypeException(Number, item2);

			var n1 : Number = _order == ORDER_ASC ? item1 : item2;
			var n2 : Number = _order == ORDER_ASC ? item2 : item1;

			if (n1 < n2) return -1;
			else if (n1 > n2) return 1;
			else return 0;
		}

	}
}

