//FILE: FanInJava1.java

package com.opt.fanin;

public class FanInJava1{
  int j;
  int i;

  FanInJava1(){
    i=0;
    j=0;
  }

  public int geti(){
    return i;
  }

  public int getj(){
    return j;
  }

  public void seti(int i){
    this.i = i;
  }

  public void setj(int j){
    this.j = j;
  }

  public static int suma(int i, int j){
    return i+j;
  }

}

