     END-PI;

    conn = JDBC_Connect(CONN_DRIVER // VIOLATION
              :'jdbc:mysql://localhost/maBase'
              :%trim(param1):%trim(param2) );
   END-PROC;
 /end-free

rule.repair:
H dftactgrp(*no)
FRANDOM_F  O    E             DISK

D CEERAN0         PR
D                               10I 0
D                                8F
D                               12A   options(*omit)

D LowNbr          S                   like(KEY)
D HighNbr         S                   like(KEY)
D Range           S                   like(KEY)
D i               S             10U 0
D RandomNbr       S              8F
D Seed            S             10I 0
/free
   HighNbr = 1000000 ;
   LowNbr = 0 ;
   Range = (HighNbr - LowNbr) + 1 ;

   for i = 1 to 1000000 ;
      CEERAN0(Seed:RandomNbr:*omit) ;
      KEY = %int(RandomNbr * Range) ;
      write RANDOM_FR ;
   endfor;

   *inlr = *on ;
/End-free

 * IBM does not provide any escape procedure for LDAP search filters
 * In ldap search filters, chars '*', '(', ')', '\', and 0 (NULL)
 * should be replaced by their \xx escape sequence
 * For dn, escape ',','=', '+', '<','>',';', '\', '"', '#'.
 * See RFC2254 for full details.

P ldap_escape     b                   export
D ldap_escape     pi         32767    varying
D   input                    32767    const options(*varsize) varying

D toEscape        c                   const('"&*()' + X'00')
D res             s          32767    varying
D i               s             10i 0
D c               s              1

  /free
    for i=1 to %len(input);
      c = %subst(input:i:1);
      if %scan(c:toEscape) > 0;
        select;
          when c = X'00';
            res = res + '\00';
          when c = '"';
            res = res + '\34';
          when c = '&';
            res = res + '\38';
          when c = '*';
            res = res + '\34';
          when c = '(';
            res = res + '\40';
          when c = ')';
            res = res + '\41';
        endsl;
      else;
        res = res + c;
      endif;
    endfor;

    return res;
  /end-free
P ldap_escape     e

rule.example:
The following pseudo-code violates the check:

// VIOLATION, no preceding SETLL
ReadE (KeyFields) FileName;
DoW Not %EOF(FIleName);
  ... process record ...
  ReadE (KeyFields) FileName;
EndDo;

rule.repair:
The following pseudo-code is conformant:

SetLL (KeyFields) FileName;
ReadE (KeyFields) FileName;
DoW Not %EOF(FIleName);
  ... process record ...
  ReadE (KeyFields) FileName;
EndDo;

rule.example:
h option(*nodebugio:*srcstmt) dftactgrp(*no) actgrp(*new) F* Externally described DSPF for user input. FDBCRED CF E WORKSTN
D SQLConnect PR Extproc('SQLConnect') D like(SQLRETURN) D hdbc like(SQLHDBC) value D szDSN * value options(*string) D cbDSN like(SQLSMALLINT) value D szUID * value options(*string) D cbUID like(SQLSMALLINT) value D szAuthStr * value options(*string) D cbAuthStr like(SQLSMALLINT) value
/FREE // read input data. Input fields: DBUSER, DBPASS, DBNAME exfmt recDbCred;
// Environment and connection prototypes / data not shown sql_rc = SQLAlcEnv(henv); sql_rc = SQLAlcCon(henv : hdbc)
// VIOLATION - resource injection on database to connect sql_rc = SQLConnect( hdbc : DBNAME : %len(DBNAME) : DBUSER : %len(DBUSER) : DBPASS : %len(DBPASS) );
/END-FREE

Select;
   When UpdByDept;
     WhereClause = 'Dept = ' + %EditC(DeptID:'X');
   When UpdByJob;
     WhereClause = 'Job = ' + %EditC(Pos:'X');
   When UpdByDate;
     WhereClause = 'HireDt < ' + %EditC(StartDate:'X');
EndSl;

Stmt = 'UPDATE EmplTable SET Sal = Sal + (Sal * '
     + %Char(RaisePct) + ') WHERE ' + WhereClause;
Exec SQL PREPARE DynUpdate from :Stmt;
// VIOLATION, potential SQL injection
Exec SQL EXECUTE DynUpdate;

rule.repair:
Select;
   When UpdByDept;
     WhereClause = 'Dept = ?';
     CompareValue = DeptID;
   When UpdByJob;
     WhereClause = 'Job = ? ' ;
     CompareValue = Pos;
   When UpdByDate;
     WhereClause = 'HireDate < ?';
     CompareValue = StartDate;
EndSl;

Stmt = 'UPDATE EmplTable SET Sal = Sal + (Sal * ?) WHERE '
     + WhereClause;

Exec SQL PREPARE DynUpdate from :Stmt;
Exec SQL EXECUTE DynUpdate Using :RaisePct, :CompareValue;

rule.example:
 // new RPG free-form format
 CTL-OPT;

 DCL-PI dbquery;
   input CHAR(10) CONST; // untrusted input
 END-PI;

 DCL-C maxCount CONST(10);
 DCL-S ssn CHAR(10);
 DCL-DS patientInfo QUALIFIED;
   firstName VARCHAR(16);
   lastName VARCHAR(26);
   age INT(3);
   medicalStatus VARCHAR(256);
 END-DS patientInfo;

 ssn = %trim(input); // untrusted input affects primary key

 // VIOLATION, untrusted input affects the key field used in the query
 EXEC SQL
   select ssn, fname, lname, age, medstat
   into
     :patientInfo.ssn, :patientInfo.firstName, :patientInfo.lastName,
     :patientInfo.age, :patientInfo.medicalStatus
   from patient_info
   where id = :ssn;

// display patientInfo ...

D/copy qsysinc/qrpglesrc,QC3CCI
D/COPY QSYSINC/QRPGLESRC,QUSEC

D* iSeries identifiers for block cipher algorithms
D DES             C                   20
D TRIPLE_DES      C                   21
D AES             C                   22

D*--- ssn = data to encrypt ---
D ssn             S            128A
D results         S          65535A
D secretKey       S             32A
D cryptoPro       S              1A
D cryptoDev       S             10A
D encryptLen      S             10I 0
D displayBytes    S             52A
D svOutData       S          65535A
D svBytes         S             52A

D*--- prototypes ---
D encryptDataAPI  PR                  extPgm('QC3ENCDT')
D  argIn                     65535    const options(*varsize)
D  argInLen                     10I 0 const
D  argInFmt                      8    const
D  argAlg                    65535    const options(*varsize)
D  argAlgFmt                     8    const
D  argKey                    65535    const options(*varsize)
D  argKeyFmt                     8    const
D  argCryptoPro                  1    const
D  argCryptoDev                 10    const
D  argOut                    65535
D  argOutBuffLen                10I 0 const
D  argOutLen                    10I 0
D  argError                           likeDs(QUSEC)
D*
 /free
  ssn = '987654320';
  secretKey = 'IamN0tSur3...';

   // Initialize the Algorithm Data Structure QC3D0200
   QC3D0200 = *AllX'00';
   QC3BCA = DES; // weak algorithm
   QC3BL = 8;
   QC3MODE = '1';
   QC3PO = '1';
   QC3EKS = 0;
   // Initialize the Key Data Structure QC3D020000 with the key
   keyDesc.QC3D020000 = *AllX'00';
   keyDesc.QC3D020000.QC3KT = argAlgorithm;
   keyDesc.QC3D020000.QC3KF = '0';
   keyDesc.QC3D020000.QC3KSL = %len(%trim(argKey));
   keyDesc.QC3Key = %trim(argKey);
   // Initialize the Rest of the Parameters
   stdError.QUSEC = *AllX'00';
   stdError.QUSEC.QUSBPRV = 1040;
   cryptoPro = '0';
   cryptoDev = *BLANKS;

   // perform encryption
   svOutData = *BLANKS;

   encryptDataAPI( // VIOLATION
     ssn: %len(ssn): 'DATA0100':
     QC3D0200: 'ALGD0200':
     keyDesc: 'KEYD0200':
     cryptoPro: cryptoDev:
     svOutData: %size(svOutData): argOutLen:
     stdError
   );

   if stdError.QUSEC.QUSBAVL > 0;
     DSPLY ('Error: ' + stdError.QUSEC.QUSEI);
     svOutData = *BLANKS;
   endif;

 /end-free
