    Runtime.getRuntime().exec(cmd); // VIOLATION
  }

  fun runProcessBuilder(cmd: String) {
    val pb = ProcessBuilder()
		pb.command(cmd) // VIOLATION
  }

  fun runKotlinNative(cmd: String) {
    platform.posix.system(cmd) // VIOLATION
  }
}

