import javax.xml.parsers.DocumentBuilderFactory

fun parse(externalInput : String): Document {
  val dbf = DocumentBuilderFactory.newInstance()

  // VIOLATION - The parser is not configured, default configuration is
  // unsafe againts XML External Entity injections
  return dbf.newDocumentBuilder().parse(StreamReader(externalInput))
}

rule.repair:
fun doc(externalInput : String) { val dbf = safeFactory()
dbf.newDocumentBuilder().parse(req.getInputStream())
val builder = dbf.newDocumentBuilder() builder.parse(req.getInputStream()) }
// FIXED - Proper configuration agains XML injections
val DOCTYPE_DECL = "http://apache.org/xml/features/disallow-doctype-decl" val EXTERNAL_ENTITITES = "http://xml.org/sax/features/external-parameter-entities" val GENERAL_ENTITIES = "http://xml.org/sax/features/external-general-entities"
fun safeFactory(): DocumentBuilderFactory { val dbf = DocumentBuilderFactory.newInstance()
dbf.setFeature(DOCTYPE_DECL, true) dbf.setFeature(EXTERNAL_ENTITITES, false) dbf.setFeature(GENERAL_ENTITIES, false) dbf.isXIncludeAware = false dbf.isExpandEntityReferences = false
return dbf }

